using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;using Microsoft.AspNetCore.Mvc;namespace data.Controllers{    class data    {        public string Fname { get; set; }        public string Lname { get; set; }        public int id { get; set; }        public string address { get; set; }    }    [Route("api/[controller]")]    public class DetailsController : Controller    {        List<data> chitti = new List<data>();        public IActionResult Index()        {            chitti.Add(new data() { Fname = "reddy", Lname = "naresh", id = 1, address = "bangalore" });            chitti.Add(new data() { Fname = "swathi", Lname = "nara", id = 2, address = "punganoor" });            chitti.Add(new data() { Fname = "hema", Lname = "reddy", id = 3, address = "chittor" });            return new JsonResult(chitti);        }    }}